module.exports = {
    calculate: function (num1, operation, num2) {
        if (typeof num1 != "number" || typeof num2 != "number") throw new Error('Please provide a valid number!');
        switch (operation) {
            case "+":
                return num1 + num2
            case "-":
                return num1 - num2
            case "*":
                return num1 * num2
            case "/":
                return num2 / num1
            case "**":
                return num1 ** num2
            default:
                throw new Error('Invalid operation! Please use +, -, *, / or **');
        }
    }
}