const boob = require("./boobs.json")
const buttys = require("./butty.json")
module.exports = {
    boobs: function(type) {
        if (!type) throw new TypeError("No category specified");
        if (type === 'real') {
            return boob.boobs[0].real.urls[Math.floor(boob.boobs[0].real.urls.length * Math.random())]
        } 
        if (type === 'hentai') {
            return boob.boobs[0].hentai.urls[Math.floor(boob.boobs[0].hentai.urls.length * Math.random())]
        }
        if (type === 'real' || type === 'hentai') {
            return
        } else {
            throw new TypeError("The category must be real or hentai")
        }
    },
    butty: function(type) {
        if (!type) throw new TypeError("No category specified");
        if (type === 'real') {
            return buttys.buttys[0].real.urls[Math.floor(buttys.buttys[0].real.urls.length * Math.random())]
        }
        if (type === 'hentai') {
            return buttys.buttys[0].hentai.urls[Math.floor(buttys.buttys[0].hentai.urls.length * Math.random())]
        }
        if (type === 'real' || type === 'hentai') {
            return
        } else {
            throw new TypeError("The category must be real or hentai")
        }
    }
}